$("#FormComentario").on('submit', function(event){
    event.preventDefault();
    var Dados = $(this).serialize();

    $.ajax({
        url:      'controllers/ctl-visitante.php',
        type:     'post',
        datatype: 'html',
        data:      Dados,
        success: function(Dados){
            $('.Resultado').show().html(Dados);
        }
    })
});